import React, { Component } from "react";
import Jumbotron from "../../Components/Jumbotron";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../Components/Grid";
import { List, ListItem } from "../../Components/List";
import { Input, TextArea, FormBtn } from "../../Components/Form";
import ClothingItem from "../../Components/ClothingItem";
const hardCodedUserId = "5db75b79c9e53d19ad99b030";
let outfits;
class Closet extends Component {
  // constructor(props) {
  //   super(props);
  //   this.loadOutfits(hardCodedUserId, array => {
  //     this.state = { outfits: array };
  //   });

  // }
  state = {
    outfits: []
  };

  componentDidMount() {
    this.loadOutfits(hardCodedUserId, array => {
      console.log(array);
    });
  }

  loadOutfits = (userId, cb) => {
    let array = [];
    console.log(userId);
    API.loadOutfits(userId)
      .then(res =>
        res.data.outfits.map(el =>
          API.getOutfit(el).then(res => {
            array.push(res.data);
          })
        )
      )
      .then(cb(array));
  };

  render() {
    const items = this.state.outfits.map(items => console.log(items));
    return (
      <Container fluid>
        <Jumbotron>
          <h1>My Closet</h1>
        </Jumbotron>

        <Row>{items}</Row>
      </Container>
    );
  }
}

export default Closet;

