import React, { Component } from "react";
import Jumbotron from "../../Components/Jumbotron";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../Components/Grid";
import { List, ListItem } from "../../Components/List";
import { Input, TextArea, FormBtn } from "../../Components/Form";
import ClothingItem from "../../Components/ClothingItem";

class Home extends Component {
  constructor(props) {
    super(props);
    this.loadClothes();
  }
  // USE DEFAULT STATE or INITIAL STATE, but for th most part just use Map, becuase it will handle the empty values... you are encountering a race condition

  state = {
    tops: [],
    bottoms: [],
    shoes: [],
    outerwear: []
  };

  componentDidMount() {
    // this.loadClothes();
  }

  loadClothes = () => {
    API.getTops()
      .then(res => this.setState({ tops: res.data }))
      .catch(err => console.log(err));
    API.getBottoms()
      .then(res => this.setState({ bottoms: res.data }))
      .catch(err => console.log(err));
    API.getShoes()
      .then(res => this.setState({ shoes: res.data }))
      .catch(err => console.log(err));
    API.getOuterwear()
      .then(res => this.setState({ outerwear: res.data }))
      .catch(err => console.log(err));
  };

  render() {
    return (
      <Container fluid>
        <Jumbotron>
          <h1>Go fishing for style</h1>
        </Jumbotron>
        <Row>
          <Col size="md-6">
            <h2>Top</h2>
            {/* {this.state.tops.map(item => (
              <ClothingItem imageURL={item.imageURL} />
            ))} */}
            {/* <ClothingItem imageURL={this.state.tops[0].imageURL} /> */}
          </Col>
          <Col size="md-6">
            <h2>Bottom</h2>
          </Col>
        </Row>
        <Row>
          <Col size="md-6">
            <h2>Outerwear</h2>
          </Col>
          <Col size="md-6">
            <h2>Shoe</h2>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Home;
