import React, { Component } from "react";
import Jumbotron from "../../Components/Jumbotron";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../Components/Grid";
import { List, ListItem } from "../../Components/List";
import { Input, TextArea, FormBtn } from "../../Components/Form";
import ClothingItem from "../../Components/ClothingItem";

class Outfit extends Component {
  state = {
    clothingType: "",
    clothingItem: "",
    top: {},
    bottom: {},
    shoe: {},
    outerwear: {},
    outfit: {
      top: {},
      bottom: {},
      shoe: {},
      outerwear: {}
    }
  };

  // get specific clothing item from id in URL path parameters
  loadClothingItem = (clothingType, idParams, cb) => {
    clothingType === "Tops"
      ? API.getTopById(idParams)
          .then(res => this.setState({ clothingItem: res.data }))
          .catch(err => console.log(err))
      : clothingType === "Bottoms"
      ? API.getBottomById(idParams)
          .then(res => this.setState({ clothingItem: res.data }))
          .catch(err => console.log(err.message))
      : clothingType === "Shoes"
      ? API.getShoeById(idParams)
          .then(res => this.setState({ clothingItem: res.data }))
          .catch(err => console.log(err))
      : clothingType === "Outerwear"
      ? API.getOuterwearById(idParams)
          .then(res => this.setState({ clothingItem: res.data }))
          .catch(err => console.log(err))
      : console.log("no clothing type specified");

    console.log(idParams);
    cb();
  };

  // load a random clothing item into the state
  loadClothes = () => {
    API.getTops()
      .then(res =>
        this.setState({
          top: res.data[Math.floor(Math.random() * res.data.length)]
        })
      )
      .catch(err => console.log(err));
    API.getBottoms()
      .then(res =>
        this.setState({
          bottom: res.data[Math.floor(Math.random() * res.data.length)]
        })
      )
      .catch(err => console.log(err));
    API.getShoes()
      .then(res =>
        this.setState({
          shoe: res.data[Math.floor(Math.random() * res.data.length)]
        })
      )
      .catch(err => console.log(err));
    API.getOuterwear()
      .then(res =>
        this.setState({
          outerwear: res.data[Math.floor(Math.random() * res.data.length)]
        })
      )
      .catch(err => console.log(err));
  };

  // this gets called when the Save Outfit button is pressed. Switch case for creating the perfect outfit.
  createOutfit = cb => {
    let top;
    let bottom;
    let shoe;
    let outerwear;
    let clothingType = this.props.match.params.clothingType;

    switch (clothingType) {
      case "Tops":
        top = this.state.clothingItem;
        bottom = this.state.bottom;
        shoe = this.state.shoe;
        outerwear = this.state.outerwear;
        break;
      case "Bottoms":
        top = this.state.top;
        bottom = this.state.clothingItem;
        shoe = this.state.shoe;
        outerwear = this.state.outerwear;
        break;
      case "Outerwear":
        top = this.state.top;
        bottom = this.state.bottom;
        shoe = this.state.clothingItem;
        outerwear = this.state.outerwear;
        break;
      case "Shoes":
        top = this.state.top;
        bottom = this.state.bottom;
        shoe = this.state.shoe;
        outerwear = this.state.clothingItem;
        break;
      default:
        top = this.state.top;
        bottom = this.state.bottom;
        shoe = this.state.shoe;
        outerwear = this.state.outerwear;
    }

    this.setState(
      state => ({
        outfit: {
          ...state.outfit,
          top: top,
          bottom: bottom,
          shoe: shoe,
          outerwear: outerwear
        }
      }),
      () => cb()
    );
  };

  saveOutfit = userId => {
    let outfit = this.state.outfit;
    API.saveOutfit(userId, outfit);
  };

  componentDidMount() {
    let idParams = this.props.match.params.id;
    let clothingType = this.props.match.params.clothingType;
    this.setState({ clothingType: clothingType });
    this.loadClothingItem(clothingType, idParams, () => {
      this.loadClothes();
    });
  }

  render() {
    return (
      <Container fluid>
        <Jumbotron>
          <h1>Your outfit, yo.</h1>
        </Jumbotron>
        <Container>
          <Row>
            <Col size={"md-6"}>
              <button
                onClick={() => {
                  this.createOutfit(
                    () => alert("callback")
                    // this.saveOutfit("5db75b79c9e53d19ad99b030")
                  );
                }}
              >
                SAVE OUTFIT
              </button>
            </Col>
            <Col size={"md-6"}>
              <button
                onClick={() => {
                  window.location.reload();
                }}
              >
                FISH AGAIN
              </button>
            </Col>
          </Row>
          <Row>
            <Col size="md-6">
              <h2>Top</h2>
              <ClothingItem
                imageURL={
                  this.state.top && this.state.clothingType === "Tops"
                    ? this.state.clothingItem.imageURL
                    : this.state.tops
                    ? this.state.tops.imageURL
                    : "error"
                }
              />
            </Col>
            <Col size="md-6">
              <h2>Bottom</h2>{" "}
              {/* <ClothingItem
                imageURL={
                  this.state.bottoms.length &&
                  this.state.clothingType === "Bottoms"
                    ? this.state.clothingItem.imageURL
                    : this.state.bottoms.length
                    ? this.state.bottoms[
                        Math.floor(Math.random() * this.state.bottoms.length)
                      ].imageURL
                    : "error"
                }
              /> */}
            </Col>
          </Row>
          <Row>
            <Col size="md-6">
              <h2>Outerwear</h2>{" "}
              {/* <ClothingItem
                imageURL={
                  this.state.outerwear.length &&
                  this.state.clothingType === "Outerwear"
                    ? this.state.clothingItem.imageURL
                    : this.state.outerwear.length
                    ? this.state.outerwear[
                        Math.floor(Math.random() * this.state.outerwear.length)
                      ].imageURL
                    : "error"
                }
              /> */}
            </Col>
            <Col size="md-6">
              <h2>Shoe</h2>{" "}
              {/* <ClothingItem
                imageURL={
                  this.state.shoes.length && this.state.clothingType === "Shoes"
                    ? this.state.clothingItem.imageURL
                    : this.state.shoes.length
                    ? this.state.shoes[
                        Math.floor(Math.random() * this.state.shoes.length)
                      ].imageURL
                    : "error"
                }
              /> */}
            </Col>
          </Row>
        </Container>
      </Container>
    );
  }
}

export default Outfit;
