import React, { Component } from "react";
import axios from "axios";
import Jumbotron from "../../Components/Jumbotron";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../Components/Grid";
import { List, ListItem } from "../../Components/List";
import { Input, TextArea, FormBtn } from "../../Components/Form";
import ClothingItem from "../../Components/ClothingItem";
import { runInThisContext } from "vm";
import { stat } from "fs";
const hardCodedUserId = "5db75b79c9e53d19ad99b030";

let outfits;
class Closet extends Component {
  state = {
    outfits: [],
    test: [1, 2, 3, 4, 5]
  };

  componentDidMount() {
    let prevState;
    API.getUser(hardCodedUserId)
      .then(response => {
        response.data.outfits.map(el => {
          API.getOutfit(el._id).then(res => {
            console.log(res.data);
            this.setState({ outfits: this.state.outfits.concat([res.data]) });
          });
        });
      })
      .catch(err => console.log(err));
  }

  // loadOutfits = (userId, cb) => {
  //   let array = [];
  //   console.log(userId);
  //   API.loadOutfits(userId)
  //     .then(res =>
  //       res.data.outfits.map(el =>
  //         API.getOutfit(el).then(res => {
  //           array.push(res.data);
  //         })
  //       )
  //     )
  //     .then(cb(array));
  // };

  render() {
    return (
      <>
        <Container fluid>
          <Jumbotron>
            <h1>My Closet</h1>
          </Jumbotron>
          <Row></Row>
        </Container>
        <p>Banana</p>
        {this.state.outfits.length}
        {this.state.outfits.map((outfit, index) => (
          <Col size="md-6">
            <Row>
              <Col size="md-6">
                {" "}
                <ClothingItem
                  imageURL={outfit.top ? outfit.top.imageURL : ""}
                />
              </Col>
              <Col size="md-6">
                {" "}
                <ClothingItem
                  imageURL={outfit.bottom ? outfit.bottom.imageURL : ""}
                />
              </Col>
            </Row>
            <Row>
              <Col size="md-6">
                {" "}
                <ClothingItem
                  imageURL={outfit.outerwear ? outfit.outerwear.imageURL : ""}
                />
              </Col>
              <Col size="md-6">
                {" "}
                <ClothingItem
                  imageURL={outfit.shoes ? outfit.shoes.imageURL : ""}
                />
              </Col>
            </Row>
          </Col>
        ))}
      </>
    );
  }
}

export default Closet;
