import React, { Component } from "react";
import { subscribeToClosetFeed } from "../../utils/ClosetFeed";
import { Link } from "react-router-dom";
import "./style.css";
import API from "../../utils/API";
class ClosetFeed extends Component {
  // constructor(props) {
  //   super(props);
  //   console.log(props);
  //   subscribeToClosetFeed((err, res) =>
  //     API.getOutfit(res._id)
  //       .then(outfit => {
  //         if (!this.state.outfitArray.includes(outfit.data)) {
  //           this.setState({
  //             outfitArray: this.state.outfitArray.concat(outfit.data)
  //           });
  //         }
  //       })
  //       .catch(err => console.log(err))
  //   );
  // }

  state = {
    outfit: false,
    outfitArray: []
  };

  componentDidMount() {
    subscribeToClosetFeed((err, res) => {
      API.getOutfit(res._id).then(outfit => {
        console.log(outfit.data);
        this.setState({
          outfit: outfit.data
        });
      });
    });
  }

  componentDidUpdate() {
    if (!this.state.outfitArray.some(e => e._id === this.state.outfit._id)) {
      this.setState({
        outfitArray: this.state.outfitArray.concat(this.state.outfit)
      });
      let outfit = this.state.outfit;
      let top = outfit.top._id;
      let bottom = outfit.bottom._id;
      let shoe = outfit.shoe._id;
      let outerwear = outfit.outerwear._id;
      let user = outfit.user._id;
      API.addToClosetFeed({
        top: top,
        bottom: bottom,
        shoe: shoe,
        outerwear: outerwear,
        user: user
      });
      this.pushToClosetFeed(this.state.outfit);
      // API.addToClosetFeed(this.state.outfit).then(res => console.log(res));
    } else {
    }
  }
  pushToClosetFeed = outfit => {
    let top = outfit.top._id;
    let bottom = outfit.bottom._id;
    let shoe = outfit.shoe._id;
    let outerwear = outfit.outerwear._id;
    let user = outfit.user._id;
    API.addToClosetFeed({
      top: top,
      bottom: bottom,
      shoe: shoe,
      outerwear: outerwear,
      user: user
    });
  };

  // }
  render() {
    return (
      //   <div className="list-overflow-container">
      //   <ul className="list-group"><li className="list-group-item">list items</li></ul>
      // </div>
      // <p className="feed">
      //   {this.state.outfit ? (
      //     <Link to={`/Closet/${this.state.outfit.user._id}/`}>
      //       {this.state.outfit.user.username} saved a new outfit!
      //     </Link>
      //   ) : (
      //     ""
      //   )}
      //   <hr></hr>
      // </p>
      <></>
    );
  }
}

export default ClosetFeed;
