  state = {
    outfit: false,
    outfitArray: []
  };

  componentDidMount() {
    subscribeToClosetFeed((err, res) => {
      API.getOutfit(res._id).then(outfit => {
        console.log(outfit.data);
        this.setState({
          outfit: outfit.data
        });
      });
    });
  }

  componentDidUpdate() {
    if (!this.state.outfitArray.some(e => e._id === this.state.outfit._id)) {
      this.setState({
        outfitArray: this.state.outfitArray.concat(this.state.outfit)
      });
      if (this.state.outfit) {
        top = this.state.outfit.data.top._id;
        bottom = this.state.outfit.data.bottom._id;
        shoe = this.state.outfit.data.shoe._id;
        outerwear = this.state.outfit.outerwear.top._id;
        user = this.state.outfit.data.user._id;
        API.pushToClosetFeed({
          top: top,
          bottom: bottom,
          shoe: shoe,
          outerwear: outerwear,
          user: user
        }).then(res => console.log(res));
      }

      // API.addToClosetFeed(this.state.outfit).then(res => console.log(res));
    } else {
    }
  }

  // pushToClosetFeed = () => {};
  render() {
    return (
