        () => {
          if (this.state.outfit.top) {
            API.addToClosetFeed({
              top: this.state.outfit.top,
              bottom: this.state.outfit.bottom,
              shoe: this.state.outfit.shoe,
              outerwear: this.state.outfit.outerwear,
              user: this.state.outfit.user
