import React, { Component } from "react";
import Jumbotron from "../../Components/Jumbotron";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../Components/Grid";
import { List, ListItem } from "../../Components/List";
import { Input, TextArea, FormBtn } from "../../Components/Form";
import ClothingItem from "../../Components/ClothingItem";
const hardCodedUserId = "5db75b79c9e53d19ad99b030";

class Closet extends Component {
  state = {
    outfits: []
  };

  componentDidMount() {
    this.loadOutfits(hardCodedUserId);
  }

  loadOutfits = userId => {
    console.log(userId);
    API.loadOutfits(userId).then(res =>
      this.setState({ outfits: res.data.outfits })
    );
  };

  render() {
    return (
      <Container fluid>
        <Jumbotron>
          <h1>My Closet</h1>
        </Jumbotron>

        <Row>
          {this.state.outfits.map(el => {
            API.getOutfit(el).then(res => {
              console.log(res.data.top);
              return (
                <Col size="md-6">
                  <Row>
                    <Col size="md-6">
                      {" "}
                      <ClothingItem imageURL={res.data.top.imageURL} />
                    </Col>
                    <Col size="md-6">
                      {" "}
                      <ClothingItem imageURL={res.data.bottom.imageURL} />
                    </Col>
                  </Row>
                  <Row>
                    <Col size="md-6">
                      {" "}
                      <ClothingItem imageURL={res.data.outerwear.imageURL} />
                    </Col>
                    <Col size="md-6">
                      {" "}
                      <ClothingItem imageURL={res.data.shoe.imageURL} />
                    </Col>
                  </Row>
                </Col>
              );
            });
          })}
        </Row>
      </Container>
    );
  }
}

export default Closet;
