import React, { Component } from "react";
import Jumbotron from "../../Components/Jumbotron";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../Components/Grid";
import { List, ListItem } from "../../Components/List";
import { Input, TextArea, FormBtn } from "../../Components/Form";

class Outfit extends Component {
  state = {
    tops: [],
    bottoms: [],
    shoes: [],
    outerwear: []
  };

  componentDidMount() {
    this.loadClothes();
  }

  loadClothes = () => {
    API.getTops()
      .then(res => this.setState({ tops: res.data }))
      .catch(err => console.log(err));
    API.getBottoms()
      .then(res => this.setState({ bottoms: res.data }))
      .catch(err => console.log(err));
    API.getShoes()
      .then(res => this.setState({ shoes: res.data }))
      .catch(err => console.log(err));
    API.getOuterwear()
      .then(res => this.setState({ outerwear: res.data }))
      .catch(err => console.log(err));
  };
  render() {
    return (
      <Container fluid>
        <Jumbotron>
          <h1>Four algorithmically generated outfits, yo</h1>
        </Jumbotron>
        <Row>
          <Col size="md-6">
            <h2>Casual</h2>
          </Col>
          <Col size="md-6">
            <h2>Hip</h2>
          </Col>
        </Row>
        <Row>
          <Col size="md-6">
            <h2>Conservative</h2>
          </Col>
          <Col size="md-6">
            <h2>Business Casual</h2>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Outfit;
