import React, { Component } from "react";
import Jumbotron from "../../Components/Jumbotron";
import API from "../../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../../Components/Grid";
import { List, ListItem } from "../../Components/List";
import { Input, TextArea, FormBtn } from "../../Components/Form";

class Outfit extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tops: []
    };
  }

  // state = { tops: [], bottoms: [], shoes: [], outerwear: [], outfits: [] };

  // state = {
  //   tops: {
  //     top1: {},
  //     top2: {}
  //   }

  state = {};

  componentDidMount() {
    let array = [];
    let tops = [];
    this.createClothesArray(array, () => {
      console.log(array);
      array.forEach(el => {
        console.log(...el);
      });

      // this.setState({ tops: array });
    });
  }

  createClothesArray = (array, cb) => {
    API.getAll(array)
      .then(
        array.forEach(el => {
          console.log(...el);
        })
      )
      .catch(err => console.log(err));
  };

  loadClothes = params => {
    API.getTops()
      .then(res => this.setState({ tops: res.data }))
      .catch(err => console.log(err));
    API.getBottoms()
      .then(res => this.setState({ bottoms: res.data }))
      .catch(err => console.log(err));
    API.getShoes()
      .then(res => this.setState({ shoes: res.data }))
      .catch(err => console.log(err));
    API.getOuterwear()
      .then(res => this.setState({ outerwear: res.data }))
      .catch(err => console.log(err));
    console.log(params);
  };
  render() {
    return (
      <Container fluid>
        <Jumbotron>
          <h1>Four algorithmically generated outfits, yo</h1>
        </Jumbotron>
        <Row>
          <Col size="md-6">
            <h2>Outfit 1</h2>
          </Col>
          <Col size="md-6">
            <h2>Outfit 2</h2>
          </Col>
        </Row>
        <Row>
          <Col size="md-6">
            <h2>Outfit 3</h2>
          </Col>
          <Col size="md-6">
            <h2>Outfit 4</h2>
          </Col>
        </Row>
      </Container>
    );
  }
}

export default Outfit;
